mapper.select:
SELECT
id,
building_id,
house_number
FROM house
WHERE building_id = #{buildingId}
AND house_number = #{houseNumber}
AND is_valid = 1  

    AND id != #{excludeId}

mapper.update:
UPDATE house
building_id = #{buildingId}, house_number = #{houseNumber}, layout = #{layout}, area = #{area}, inner_area = #{innerArea}, floor = #{floor}, total_floor = #{totalFloor}, orientation = #{orientation}, decoration = #{decoration}, house_type = #{houseType.code}, price = #{price}, status = #{status.code}, owner_price = #{ownerPrice}, description = #{description}, agent_id = #{agentId}, is_valid = #{isValid}, updated_at = #{updatedAt}, updated_by = #{updatedBy}
WHERE id = #{id}

mapper.select:
SELECT id, building_id, house_number, layout, area, inner_area, floor, total_floor, orientation, decoration, house_type, price, status, owner_price, description, agent_id, is_valid, created_at, updated_at, created_by, updated_by FROM house WHERE is_valid = 1
AND building_id = #{buildingId}
AND house_number LIKE CONCAT('%', #{houseNumber}, '%')
AND layout LIKE CONCAT('%', #{layout}, '%')
AND house_type = #{houseType} AND price >= #{priceStart} AND price <= #{priceEnd} AND status = #{status} AND floor >= #{floorStart} AND floor <= #{floorEnd} AND agent_id = #{agentId}
ORDER BY updated_at DESC
